update.foreach:
UPDATE ORDER_HISTORY AS OH JOIN STOCK AS STK ON OH.PDC_CODE = STK.PDC_CODE
AND OH.PD_CODE = STK.PD_CODE
SET STK.STK_STOCK = (STK.STK_STOCK-(#{odr.oh_cnt}))
WHERE OH.C_CODE =#{odr.c_code} AND OH.OAC_NUM = #{odr.oac_num} AND
OH.PDC_CODE = #{odr.pdc_code}
AND OH.PD_CODE = #{odr.pd_code} AND STK.STK_STOCK >=0;
